mapper.update:
update items
channel_uuid = #{channelUuid}, uuid = #{uuid}, title = #{title}, image = #{image}, description = #{description}, link = #{link}, enclosure = #{enclosure}, create_time = #{createTime}, status = #{status}, duration = #{duration}, downloader = #{downloader}, file_name = #{fileName}, format = #{format}, download_progress = #{downloadProgress}, download_time_left = #{downloadTimeLeft}, total_size = #{totalSize}, download_speed = #{downloadSpeed}, operation = #{operation}, type = #{type}, args = #{args}, links = #{links}, public_time = #{publicTime},
where uuid = #{uuid}

mapper.select:
select * from items


        and channel_uuid = #{channelUuid}


        and uuid = #{uuid}


        and status = #{status}
